function createSubTitle($baseTime, $subs){
	$ln = "\r\n";
	$subtitle = "";

	for ($i = 1; $i <= count($subs); $i++)
	{
		$item = $subs[$i-1];
		$start = $item->startTime + $baseTime;
		
		$subtitle  .= ($i) . $ln;
		$subtitle  .= getTime($start, $start + $item->duration) . $ln;
		$subtitle  .= $item->content . $ln;
		$subtitle  .= $ln;
	}
	return $subtitle;
}

function getTime($t1, $t2){
	return formatTime($t1) . " --> " . formatTime($t2);
}

function formatTime($t1){
	$s1 = floor($t1 / 1000);
	$m1 = floor($s1 / 60);
	$h1 = floor($m1 / 60);
	$m1 = $m1 - $h1 * 60;
	$s1 = $s1 - $h1 * 3600 - $m1 * 60;
	$t1 = $t1 - $h1 * 3600000 - $m1 * 60000 - $s1 * 1000;
	$s = $h1 . ":" . $m1 .":" . $s1 . "," . floor($t1);

	return $s;
}
		
function getPageSource($url){
	return file_get_contents($url);
}

function fiindTitle($source){
	$ind1 = strpos($source, '<title>');
	if($ind1 < 0){
		return "No Title";
	}
	$ind2 = strpos($source, '</title>');
	return substr($source, $ind1 + 7 , $ind2 - $ind1 - 7);
}

function findId($source){
    $shortUrlRegex = '/\/\/talks\/([0-9]+)\?source/i';
	
    if (preg_match($shortUrlRegex, $source, $matches)) {
        $youtube_id = $matches[count($matches) - 1];
		return $youtube_id;
    }
	
	return null;
}

function getInfo($vid){
	$infoUrl = 'https://hls.ted.com/talks/'.$vid.'.json';
	//todo
	//$infoSource = getPageSource($infoUrl);
	$infoSource = '{"timing":{"intro":{"start":0,"end":12.004729999999999},"content":{"start":12.004729999999999,"end":765.1668999999996},"outro":{"start":765.1668999999996,"end":766.2814599999996}}}';
	if($infoSource == null || strlen($infoSource) <= 0)
		return null;
	return json_decode($infoSource);
}


